export function getToken(path) {
  return (path || "").split(".").reduce((acc, key) => {
    if (acc == null) return acc;
    return acc[key];
  }, tokens);
}

export function cssVar(path) {
  return `var(--${(path || "")
    .split(".")
    .map(toKebabCase)
    .join("-")})`;
}
